# PUT THE PROPER PROVIDER
web3 = Web3(Web3.HTTPProvider("https://bscrpc.com"))

# PUT YOUR TOKEN ADDRESS ONLY
TokenAddress = "0x4ceaCF951294f78bde6B51863aF8fDC03d54728e"

contract_address = web3.toChecksumAddress(TokenAddress)

contract = web3.eth.contract(address=contract_address, abi=abi)
decimals = contract.functions.decimals().call()
circulatingSupply = contract.functions.getCirculatingSupply().call() / \
    10**decimals

# INSERT BOT TOKEN FROM BOT FATHER AND YOUR GROUP ID
bot = telebot.TeleBot(
    "5744489424:AAGdGlwuwqlMC8C_Vg4_W9mpHmePJgXmNyY", parse_mode="MARKDOWN")
group_id = -1001664909588
buyBotVideo = "https://i.imgur.io/TI5LPSS.mp4"
groupTG = "@safesurge"

# latest Blocks and Txs
latestBlock = web3.eth.block_number
lastTx = contract.functions.totalTx().call()

# Only change the emoji if you want
while (True):
    dumBlock = web3.eth.block_number
    newLastTx = contract.functions.totalTx().call()

    if (newLastTx > lastTx):
        events = contract.events.Bought.getLogs(
            fromBlock=latestBlock+1, toBlock=dumBlock)

        if (len(events) != 0):
            for event in events:
                buy = event['args']['beans']/10**9
                dollarBuy = event['args']['dollarBuy']/10**18/10**18
                got = event['args']['tokens']/10**decimals
                marketCap = dollarBuy/got*circulatingSupply
                price = dollarBuy/got
                priceSRG = buy/got
                txHash = web3.toHex(event['transactionHash'])
                balance = contract.functions.balanceOf(
                    event['args']['from']).call()/10**decimals
                bPos = "New!" if (
                    balance-got) == 0 else f'+{got/(balance-got):.0%}'
                bot.send_video(chat_id=group_id,
                               caption="*SAFESURGE BSC BUY!*\n" +
                               "⚡️" * min(math.ceil(buy/500), 250)+"\n" +
                               "*Spent:* " + f'${dollarBuy:,.2f} ({buy:,.2f} SRG)\n' +
                               f"*Got*: {got:,.2f} SAFESURGE\n" +
                               f"*Buyer Position:* {bPos}\n" +
                               f'*Price:* ${price:,.5f} ({priceSRG:,.5f} SRG)\n' +
                               f'*MCap:* ${marketCap:,.2f}\n' +
                               f"*TG:* {groupTG}\n" +
                               f'[TX](https://bscscan.com/tx/{txHash})', parse_mode="MARKDOWN", video=buyBotVideo)
                
                lastTx = newLastTx

    latestBlock = dumBlock
    time.sleep(10)
    pass
